import React, { useState } from 'react';
import './cell.css';
import styled from 'styled-components';



type CellProps = {
  id: string;
};
export const Cell = ({ id }: CellProps) => {
  const [isBlack,setIsBlack] = useState<"black" | "white" | null>(null)

  const handleSelect = () => {
setIsBlack()
  }

  return (
    <SButton $isBlack={isBlack} onClick={handleSelect} className="square">
      {id}
    </SButton>
  );
};
export default Cell;

const SButton = styled.button<{$isBlack: "black" | "white" | null}>`
  background: ${(props) => props.$isBlack === "black" ? "black": props.$isBlack === "white" ?  "white" : "#008000"};
  border: 3px solid #000;
  height: 12.5%;
  width: 12.5%;
  cursor: pointer;
`;
