import React, { useState } from 'react';
import './cell.css';
import styled from 'styled-components';



type CellProps = {
  id: string;
  count: number;
  countUp: React.Dispatch<React.SetStateAction<number>>
};
export const Cell = ({ id, count, set }: CellProps) => {
  const [isBlack,setIsBlack] = useState<"black" | "white" | null>(null);
  [count, setCount] = useState<number>(0)


  const countUp = () => {
    setCount(count + 1)
  }

  const handleSelect = () => {
    if(isBlack === null){
      countUp()
      {count % 2 === 0 ? setIsBlack('white') : setIsBlack('black')}
      console.log(count)
    }else{
      alert('そこに置くことはできません')
    }
  }

  return (
    <SButton $isBlack={isBlack} onClick={handleSelect} >
      {id}
    </SButton>
  );
};
export default Cell;

const SButton = styled.button<{$isBlack: "black" | "white" | null}>`
  background: ${(props) => props.$isBlack === "black" ? "black": props.$isBlack === "white" ?  "white" : "#008000"};
  border: 3px solid #000;
  height: 12.5%;
  width: 12.5%;
  cursor: pointer;
`;
