import React, { useEffect, useState } from 'react';
import './cell.css';
import styled from 'styled-components';

type CellProps = {
  id: string;
  count: number;
  setCount: React.Dispatch<React.SetStateAction<number>>;
  setIsColor: React.Dispatch<React.SetStateAction<"black" | "white" | null>>
  isColor: "black" | "white" | null;
};
export const Cell = ({ id, count, setCount, isColor, setIs }: CellProps) => {

  const countUp = () => {
    setCount(count + 1)
  }

  const handleSelect = () => {
    if(isBlack === null){
      countUp()
      {count % 2 === 0 ? setIsBlack('white') : setIsBlack('black')}
    }else{
      alert('そこに置くことはできません')
    }
  }

  useEffect (() => {
    if(id === '28' || id === '37'){
      setIsBlack('black')
    }
  }, [])

  return (
    <SButton $isBlack={isBlack} onClick={handleSelect} >
      {id}
    </SButton>
  );
};
export default Cell;

const SButton = styled.button<{$isBlack: "black" | "white" | null}>`
  background: ${(props) => props.$isBlack === "black" ? "black": props.$isBlack === "white" ?  "white" : "#008000"};
  border: 3px solid #000;
  height: 12.5%;
  width: 12.5%;
  cursor: pointer;
`;
